C++<pre><code>int gcd(const int a, const int b) {
    return (b == 0) ? a : gcd(b, a % b);
}
</code></pre>

C++<pre><code>int lcm(const int a, const int b) {
    return a * b / gcd(a, b);
}
</code></pre>

C++<pre><code>#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;iostream&gt;
#include &lt;vector&gt;

std::vector&lt;int&gt; getFactors(const int n) {
    std::vector&lt;int&gt; factors;
    if (n &lt;= 0) return factors;
    for (int i = 1; i &lt;= std::sqrt(n); ++i) {
        if (n % i == 0) {
            factors.push_back(i);
            if (i != n / i) {
                factors.push_back(n / i);
            }
        }
    }
    std::sort(factors.begin(), factors.end());
    return factors;
}

int main() {
    for (int f : getFactors(12345)) {
        std::cout &lt;&lt; f &lt;&lt; ' ';
    }
    return 0;
}

// 输出：1 3 5 15 823 2469 4115 12345
</code></pre>

